// Classify intent using advanced patterns
function classifyIntent(text) {
  const patterns = {
    greeting: /\b(hi|hello|hey|start|good\s+(morning|afternoon|evening))\b/i,
    demo_request: /\b(demo|demonstration|show|see\s+platform|trial)\b/i,
    pricing_inquiry: /\b(price|pricing|cost|how\s+much|plans|subscription)\b/i,
    feature_question: /\b(features|capabilities|what\s+can|functionality)\b/i,
    support_request: /\b(help|support|problem|issue|trouble)\b/i,
    lead_info: /\b(contact|email|phone|reach|business|company)\b/i,
    refund_inquiry: /\b(refund|money\s+back|return|cancel|guarantee)\b/i,
    booking: /\b(book|schedule|appointment|meeting|call)\b/i
  };

  for (const [intent, pattern] of Object.entries(patterns)) {
    if (pattern.test(text)) {
      return intent;
    }
  }

  return 'general_inquiry';
}

const intent = classifyIntent(message.text);

return [{
  json: {
    chat_id: message.chat.id,
    user_id: message.from.id,
    username: message.from.username || 'Unknown',
    message_text: message.text,
    intent: intent,
    timestamp: new Date().toISOString(),
    message_id: message.message_id
  }
}];

